sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate) {
		// Create GlideDateTime objects for the current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// Return true if the testdate is before rightnow
		return testdate.before(rightnow);
	},

	isDateToday: function(strDate) {
		// Create GlideDateTime objects fo rhte current date and the passed in date
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);
		// Get the date portion of rightnow and test date (no timestamp)
		var today = rightnow.getLocalDate();
		var istoday = testdate.getLocalDate();

		// Return true if today and istoday are the same day
		return today.compareTo(istoday) == 0;
	},

    type: 'NeedItUtils'
};
